    }
  }
  return (
    <form onSubmit={validateForm}>
      <h2>SignUp</h2>
      <div>
        <label for="full-name">Full Name</label>
        <input
          id="full-name"
          placeholder="Enter your fullname"
          type="text"
          onClick={(e) => setFullName(e.target.value)}
        />
      </div>
      <div>
        <label for="email">Email</label>
        <input
          id="email"
          placeholder="Enter email"
          type="email"
          onClick={(e) => setEmail(e.target.value)}
        />
      </div>
      <div>
        <label for="password">Password</label>
        <input
          id="password"
          placeholder="Enter password"
          type="password"
          onClick={(e) => setPassword(e.target.value)}
        />
      </div>
      <div>
        <label for="confirm-password">Confirm Password</label>
        <input
          id="confirm-password"
          placeholder="Enter confirm password"
          type="password"
          onClick={(e) => setConfirmPassword(e.target.value)}
        />
      </div>
      <div className="error-success-msg">
        {errorMsg} && <p id="error-msg">All the fields are mandatory</p>
        <p id="success-msg">Successfully SignedUp!!</p>
      </div>
      <input type="submit" value="SignUp" id="signup-btn" />
    </form>
  );
}
