import React, { useState } from "react";

export default function SignUp() {
  const [fullName, setFullName] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [confirmPassword, setConfirmPassword] = useState("");
  const [errorMsg, setErrorMsg] = useState(false);
  const [passwordMismatchMsg, setPasswordMismatchMsg] = useState(false);
  const [successMsg, setSuccessMsg] = useState(false);

  //Form validation(Error/Success Handling)
  console.log()
  function validateForm() {
    if (!fullName) {
        setErrorMsg(!errorMsg)
        return
    }
    else if (!email) {
        setErrorMsg(!errorMsg)
        return
    } else if (!password) {
        setErrorMsg(!errorMsg)
        return
    }
    else if (!confirmPassword) {
        setErrorMsg(!errorMsg)
        return
    }
    else if (password !== confirmPassword) {
        setPasswordMismatchMsg(!passwordMismatchMsg)
        return
    }
    else {
        setSuccessMsg(!successMsg)
    }
  }
  return (
    <form onSubmit={(e) => {
        e.preventDefault()
        validateForm()
    }}>
      <h2>SignUp</h2>
      <div>
        <label for="full-name">Full Name</label>
        <input
          id="full-name"
          placeholder="Enter your fullname"
          type="text"
          onClick={(e) => setFullName(e.target.value)}
        />
      </div>
      <div>
        <label for="email">Email</label>
        <input
          id="email"
          placeholder="Enter email"
          type="email"
          onClick={(e) => setEmail(e.target.value)}
        />
      </div>
      <div>
        <label for="password">Password</label>
        <input
          id="password"
          placeholder="Enter password"
          type="password"
          onClick={(e) => setPassword(e.target.value)}
        />
      </div>
      <div>
        <label for="confirm-password">Confirm Password</label>
        <input
          id="confirm-password"
          placeholder="Enter confirm password"
          type="password"
          onClick={(e) => setConfirmPassword(e.target.value)}
        />
      </div>
      <div className="error-success-msg">
        {errorMsg && <p id="error-msg">All the fields are mandatory</p>}
        {passwordMismatchMsg && <p id="error-msg">Password and Confirm Password not matching</p>}
        {successMsg && <p id="success-msg">Successfully SignedUp!!</p>}
      </div>
      <input type="submit" value="SignUp" id="signup-btn" />
    </form>
  );
}
